from flask import Flask

app = Flask(__name__)

@app.route("/")
def hello_world():
    return "<h1 style='text-align: center;'>Hello, World!</h1>" \
        "<p>This is a paragraph.</p>" \
        "<img src='data:image/jpeg;base64,' width=200>" \
        "<img src='https://media3.giphy.com/media/v1.Y2lkPTc5MGI3NjExdTM5MWZ5NnRsbXpnaGw1Mnh1dHkxc2FkaTE1OHoxZmt0MG00ZXc1ZiZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/3oriO0OEd9QIDdllqo/giphy.gif', width=200>"

def make_bold(function):
    def wrapper():
        return "<b>" + function() + "</b>"
    return wrapper

def make_emphasis(function):
    def wrapper():
        return f"<em>{function()}</em>"
    return wrapper

@app.route("/bye")
@make_bold
@make_emphasis
def say_bye():
    return "Bye"

