# @app.route("/<path:name>")
# def greet(name):
#     return f"Hello {name}!"

@app.route("/<name>/<int:number>")
def greet(name, number):
    return f"Hello {name}! You are {number} years old!"

if __name__ == "__main__":
    app.run(debug=True)